import React, { useState,useEffect,useRef, Fragment} from 'react';
import {Row, Col, Empty, Modal, Card, Typography, Alert,Form,Input, Checkbox,Button,Switch, Upload, Dropdown, Menu, Select, notification, Transfer, DatePicker, Avatar, message, Descriptions } from 'antd';
import { LeftOutlined, LoadingOutlined, EditOutlined, CloseOutlined} from '@ant-design/icons';
import {section , semester} from '../../../utils/section'
import moment from 'moment';
import { connect } from 'dva';
import styles from './style.less';

const { Text } = Typography;
const { TextArea } = Input;
const timestemp = (new Date()).getTime();
const { RangePicker } = DatePicker;
const formItemLayout = { labelCol: {xs: { span: 24, },  sm: { span: 24, },}};
const dateFormat = 'YYYY/MM/DD';

const AddEditStudent =props => {
	const [form] = Form.useForm();
	const { dispatch, category } = props;
	const [visible, setVisible] = useState(false);
	const [detail, setDetail] = useState({});
	const [itemId, setItemId] = useState()
	const [imageUrl, setImageUrl] = useState('');
	const [picModel, setPicModel] = useState(false);
	const [loading, setLoading] = useState(false);
	const [InquiryData, setInquiryData] = useState(''); 
	const [Inquiry, setInquiry] = useState('');
	const [count, setCount] = useState(0)
	const [ecount, setECount] = useState(0)
	const [dcount, setDCount] = useState(0)
	const [btnDis, setBtnDis] = useState(false)



	
	
	useEffect(() => {
		let unmounted = false;
		window.scroll(0, 0);
		if(props.match && props.match.params.id)
		{
			setItemId(props.match.params.id)
			DetailFun(props.match.params.id)

		}else {
			form.resetFields();
			setImageUrl('');
		}
		return () => { unmounted = true; }
    },[dispatch])
	
	
	const DetailFun=(id)=>{
		props.dispatch({type: 'users/getDetail', payload: { _id: id, profile_id: id }});
	}
	
	const onFinish= val=>{
		props.dispatch({type: 'users/updateSection', payload: { _id: props.match.params.id, semester  : val.semester , section  : val.section }});
		form.setFieldsValue({
			 ['semester']: val.semester ,  ['section']: val.section
		  });

	}
	
	useEffect(() => {
		let unmounted = false;
		// Edit
		let edit = props.users.edit
		if(!unmounted && edit.count > ecount && edit.status){
		  setECount(edit.count);
		  setBtnDis(false);
		  setImageUrl('');
		  cancelFun();
		}else if(!unmounted && edit.count > ecount && !edit.status){
		  setBtnDis(false);
		  setECount(edit.count);
		}

		
		
		// detail
		if(props.match && props.match.params.id)
		{
			
			let detail = props.users.detail;
			console.log("detail : ", detail);
			if(!unmounted &&  detail && detail.status){
			  setDCount(detail.count);
			  let data = detail.profile;
				setDetail({
					...data,
					username: data.username,
					email: data.email,
					mobile_number: data.mobile_number,
					isEmailVerified: data.isEmailVerified,
					isMobileVerified: data.isMobileVerified,
					roles: data.roles,
				})
				form.setFieldsValue({
				  ['dob']: moment(new Date(data.dob), 'YYYY/MM/DD'),  ['email']: data.email, ['gender']: data.gender, ['name']: data.name, ['phone']: data.phone, ['photo']: data.photo , ['semester']: data.semester ,  ['section']: data.section
				});

				setImageUrl(data.photo);
			}else if(!unmounted && detail && !detail.status){
			  setBtnDis(false);
			  setDCount(detail.count);
			}
		}
		return () => {unmounted = true;	}

    },[props.users])
	
	const cancelFun = ()=>{
		form.resetFields();
		setImageUrl('');
		props.history.push('/students');
	}
	const getNewImage = val =>{
		setPicModel(false);
		setImageUrl(val[0])
		form.setFieldsValue({images:val})
	}
	const removeImgFun=()=>{
		setImageUrl('');
		form.setFieldsValue({images:[]});
	}
	const uploadButton = (
      <div>
		{loading ? <LoadingOutlined /> : <i className="fad fa-camera-retro" style={{color:"#13c2c2", fontSize: 57}} />}
      </div>
    );
	
return (
	<> 
	<Card title={<span><LeftOutlined onClick={()=> props.history.push('/students')}/> User Details</span>} style={{marginTop:"0"}}>
	<Row gutter={15} className='mb-4'>
				<Col flex="auto">
						<Row gutter={100}>
						<Col sm={12} md={12}>
						<h4><span class="label label-default">Profile Image</span></h4>
						<Avatar shape="round" size={160} src={detail.avatar} style={{marginBottom: '2px'}} />

							</Col>

							<Col sm={12} md={12}>
							<h4><span class="label label-default">ID Card</span></h4>


							<Avatar shape="square" size={160} src={detail.id_card} style={{marginBottom: '2px'}} />

							</Col>
                           
						</Row>
					</Col>
				</Row>

	{/* <Avatar shape="round" size={160} src={detail.avatar} style={{marginBottom: '2px'}} />
	<Avatar shape="square" size={160} src={detail.id_card} style={{marginBottom: '2px'}} /> */}

		<Descriptions size={'middle'} bordered>					
          <Descriptions.Item label="Name">{detail.username}</Descriptions.Item>
		  <Descriptions.Item label="Email">{detail.email}</Descriptions.Item>
		  <Descriptions.Item label="Is Email Verified">{detail.isEmailVerified ? 'true' : 'false'}</Descriptions.Item>
		  <Descriptions.Item label="Role">{detail.roles}</Descriptions.Item>
          <Descriptions.Item label="Profile Created On">{moment(detail.create).format(dateFormat)}</Descriptions.Item>
		  <Descriptions.Item label="DOB">{detail.dob}</Descriptions.Item>
		  <Descriptions.Item label="Mobile No">{detail.mobile_number}</Descriptions.Item>


		  
        </Descriptions>
	</Card>
	

	<Card title={<span> Class Details</span>} style={{marginTop:"30px"}}>
	<Form {...formItemLayout} form={form} name="loc_info" layout="vertical" onFinish={onFinish} className="innerFields">

				<Row gutter={15}>
				<Col flex="auto">
						<Row gutter={15}>
						<Col sm={12} md={12}>
								<Form.Item name="semester" label="semester" rules={[{ required: true, message: 'This field is required!' }]}  >
								<Select placeholder="Select semester" >
										{semester && semester.map((item, index) => <Select.Option key={index} value={item}>{item}</Select.Option>)}
									</Select>
								</Form.Item>
							</Col>

							<Col sm={12} md={12}>
								<Form.Item name="section" label="Section" rules={[{ required: true, message: 'This field is required!' }]}  >
								<Select placeholder="Select Section" >
										{section && section.map((item, index) => <Select.Option key={index} value={item}>{item}</Select.Option>)}
									</Select>
								</Form.Item>
							</Col>
                           
						</Row>
