import React from 'react';
import { Link } from 'react-router-dom';
import Apploader from '../../components/loader/loader'
import { connect } from 'dva';
import moment from 'moment';

import { Empty, Card, Typography, Alert, Input, Button, Table, Radio, Divider, Switch, Row, Col, Avatar, Pagination, Tabs, Modal, Badge, Popconfirm } from 'antd';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import AddEdit from './action/addEdit';
import { getTitleImage } from '../../utils/functions';
const { Search } = Input;
const { Text } = Typography;
const { TabPane } = Tabs;


class TimeTableList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			limit: 25, 
			current: 1, 
			sortBy: 'asc', 
			addModel: false, 
			inactive: false, 
			searchText: '', 
			loader: false, 
			detail: '', 
			count: 0, 
			Addcount: 0,
			listData: []
		}

		setTimeout(() => document.title = 'Time Table List', 100,);
		this.isUpdate = false;
	}
	
	componentDidMount() {
		this.ListFun();


		
	}


	
	ListFun = () => {
		this.props.dispatch({ type: 'time_tables/timeTableList', payload: {} });
	}

	ShowSizeChange = (current, size) => this.setState({ limit: size }, () => this.ListFun());
	switchFun = (val) => this.setState({ inactive: val }, () => this.ListFun());
	ChangeOrder = (val) => this.setState({ sortBy: this.state.sortBy === 'asc' ? 'desc' : 'asc' }, () => this.ListFun());

	searchVal = (val) => {
		this.state.searchText = val;
		const resultAutos = this.props.time_tables.list.filter((auto) => 
									auto.title.toLowerCase().includes(val.toLowerCase()) || 
									auto.description.toLowerCase().includes(val.toLowerCase())
							)
		this.setState({ listData: resultAutos })
	}

	createCat = (val) => {
		if (val) { this.ListFun() }
		this.setState({ detail: '', addModel: false })
	}

	deletetime_tables = id => {
		this.props.dispatch({ type: 'time_tables/deleteTimeTable', payload: id });
	}

	getSnapshotBeforeUpdate(prevProps, prevState) {
        if ( this.props.time_tables.delete) {
			this.props.dispatch({ type: 'time_tables/clearAction'});
            this.ListFun();
			return true
        }
        return null;
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (snapshot) {
        }
    }

	render() {
		const { inactive, limit, searchText, addModel, detail } = this.state;
		const { time_tables } = this.props;
		if (this.state.searchText == '') {
			this.state.listData = time_tables.list ? time_tables.list : [];
		}

		const columns = [
			
			{ title: <strong>Stream Name</strong>, dataIndex: 'streamInfo',
				render: (value, row) => {
					return <span>{value[0]?.name}</span> 
				}
			},
			{
				title: <strong>Year</strong>,
				dataIndex: 'year'
			},{
				title: <strong>class</strong>,
				dataIndex: 'class'
			},
			{ title: <strong>Subject Name</strong>, dataIndex: 'subjectInfo',
			render: (value, row) => {
				return <span>{value?.subject_name}</span> 
			}
		},
		{ title: <strong>Teacher Name</strong>, dataIndex: 'userInfo',
		render: (value, row) => {
			return <span>{value[0]?.username}</span> 
		}
	},
	{ title: <strong>Start Time</strong>, dataIndex: 'startTime',
	render: (value, row) => {
		return <span>{value}</span> 
	}
},
{ title: <strong>End Time</strong>, dataIndex: 'endTime',
render: (value, row) => {
	return <span>{moment(value)}</span> 
}
},
			{
				title: <strong>Action</strong>,  align: 'center',
				render: (val, data) => <div onClick={e => e.stopPropagation()}>
					<Button type="default" className="edit_btn" onClick={()=>{this.props.history.push('/time-table/edit/' + data._id )}}><EditOutlined /></Button>
					<Popconfirm title="Are you sure delete this time table?" onConfirm={e => { this.deletetime_tables(data._id); e.stopPropagation() }} okText="Yes" cancelText="No" >
						<Button type="danger" ><DeleteOutlined /></Button>
					</Popconfirm>
				</div>
			},
		];
		return (
			<div>
				<Apploader show={this.props.loading.global} />
				<Row className="TopActionBar" gutter={[16, 0]} justify="space-between" align="middle">
					<Col>
						<Search placeholder="Search..." onChange={(e) => this.searchVal(e.target.value)} value={searchText} />
					</Col>
					<Col>
						<Button type="primary" onClick={() => this.props.history.push('/time-table/add')  }>Add</Button>
					</Col>
				</Row>

				<div className="innerContainer">
					<Card style={{ marginTop: "0" }} bodyStyle={{ padding: '0 15px 15px' }}>
						<Table columns={columns} dataSource={this.state.listData}
							rowKey={record => record._id}
							onRow={(record, rowIndex) => {
								return {
									// onClick: event => this.setState({ addModel: true, detail: record })
								};
							}}
							pagination={{
								position: ['bottomLeft'],
								showTotal: (total, range) => <Text type="secondary">{`Showing ${range[0]}-${range[1]} of ${total}`}</Text>, showSizeChanger: true,
								responsive: true,
								onShowSizeChange: (current, size) => this.ShowSizeChange(current, size),
								pageSizeOptions: ['10','25', '50', '100', '250', '500'],
							}}
						/>
					</Card>
				</div>

			</div>

		);
	}
};

export default connect(({ time_tables, loading }) => ({
	time_tables, loading
}))(TimeTableList);
