import React, { useState, useEffect, useRef, Fragment } from 'react';
import { Row, Col, Empty, Modal, Card, Typography, Alert, Form, Input, Checkbox, Button, Space, Upload, Dropdown, Menu, Select, notification, Transfer, DatePicker, TimePicker, message, InputNumber } from 'antd';
import { LeftOutlined, LoadingOutlined, EditOutlined, CloseOutlined, PlusOutlined, MinusCircleOutlined } from '@ant-design/icons';
import UploadImages from '../../../components/sharing/upload-images'
import CropImage from '../../../components/sharing/crop-image'
import TextEditor from '../../../components/sharing/text-editor'
import moment from 'moment';
import { connect } from 'dva'; 
import styles from './style.less'; 
import { getSubCatbyCategory } from '../../../services/api'
import MultiImageInput from 'react-multiple-image-input';
import HTMLDecoderEncoder from 'html-encoder-decoder';

const { Text } = Typography;
const { TextArea } = Input;
const timestemp = (new Date()).getTime();
const { RangePicker } = TimePicker;

const formItemLayout = { labelCol: { xs: { span: 24, }, sm: { span: 24, }, } };
const baseUrl = process.env.REACT_APP_ApiUrl
const AddEditTimeTable = props => {
	const [form] = Form.useForm();
	const { dispatch,subjects } = props;
	const [Inquiry, setInquiry] = useState('');
	const [PageId, setPageId] = useState('');
	const [count, setCount] = useState(0)
	const [yearList, setYearList] = useState([])
	const [classList, setClassList] = useState([])

	const classArray = subjects?.listData?.data?.class_list;
	const teacherArray = subjects?.listData?.data?.teacher_list;
	const subjectArray = subjects?.listData?.data?.subject_list;
    const streamArray = subjects?.listData?.data?.stream_list

let array = []

// console.log('subjectArray' , subjects)



	useEffect(() => {
		let unmounted = false;
        let user_id = localStorage.getItem('userId');
		props.dispatch({ type: 'subjects/getsubjectList', payload: {user_id : user_id} }); 

		window.scroll(0, 0);
		if (props.match.params.id) {
			DetailFun(props.match.params.id)
		} else {
			form.resetFields();
		}
		return () => { unmounted = true; }
	}, [dispatch])
	
	const DetailFun = (id) => {
		props.dispatch({ type: 'pages/pagesDetail', payload: id });
	}

	useEffect(() => {
		let unmounted = false;

		if(props.pages.add){
			dispatch({ type: 'pages/clearAction'});
			props.history.push('/pages');
		}
		
		if(props.pages.edit){
			dispatch({ type: 'pages/clearAction'});
			props.history.push('/pages');
		}
		
		if(props.pages && props.pages.detail && props.pages.detail.status){
			let data = props.pages.detail.data[0];
			setPageId(data._id)
			// setInquiry(HTMLDecoderEncoder.decode(data.html));
			form.setFieldsValue({
				['title']: data.title, 
				['description']: data.description, 
				['isActive']: data.isActive, 

			})
		}

		return () => { unmounted = true; }
	}, [props.pages])

	const cancelFun = () => {
		form.resetFields();
		props.history.push('/pages');
	}

	const onFinish = val => {
		// val.html = HTMLDecoderEncoder.encode(Inquiry);
		// val = convertUndefinedObjectKeysToEmptyString(val);

		console.log('onFinish', val)

		return;

		if (props.match.params.id) {
			val._id = PageId;

			dispatch({ type: 'pages/EditPages', payload: val });
		}else {
			dispatch({ type: 'pages/AddPages', payload: val });
		}
	}

	const convertUndefinedObjectKeysToEmptyString = (object) => {
		var output = {};
		for(let i in object) {
			if(!object[i]) {
				output[i] = "";
			} else {
				output[i] = object[i];
			}	
		}
		return output;
	}

	const onStreamChange = async (e, needUpdate_ = false) => {	
		setYearList([])
const year_list = [...new Map(classArray && classArray.filter((item) => item.streamInfo._id === e).map((item) => [item["year"], item])).values(),];
setTimeout(() => {
	setYearList(year_list)

}, 100)
	}
	const onYearChange = async (e, needUpdate_ = false) => {	
		// const class_list = [...new Map(classArray && classArray.filter((item) => item.streamInfo._id === e).map((item) => [item["class"], item])).values(),];
		console.log("onYearChange" ,classArray)
		setClassList(classArray)
			}




	
	return (
		<Card title={<span><LeftOutlined onClick={() => props.history.push('/pages')} /> 
			{ props.detail ? 'Edit Time Table' : 'Add Time Table'}</span>} style={{ marginTop: "0" }}>

			<Form {...formItemLayout} form={form} name="loc_info" layout="vertical" onFinish={onFinish} className="innerFields">
			
				<Row gutter={15}>
					<Col sm={24} md={12}>
						<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
								<Select placeholder="Select Stream"  onChange={(e) => onStreamChange(e)}>
										{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={item?.streamInfo?._id}>{item?.streamInfo?.name}</Select.Option>)}
									</Select>
						</Form.Item>
					</Col>

					<Col sm={24} md={12}>
						<Form.Item name="year" label="Select Year" rules={[{ required: true, message: 'Field required!' },]}  >
								<Select placeholder="Select Year" onChange={(e) => onYearChange(e)}>
										{yearList && yearList.map((item, index) => <Select.Option key={index} value={item.year}>{item.year}</Select.Option>)}
									</Select>
						</Form.Item>
					</Col>
				</Row>


				<Row gutter={15}>
					<Col sm={24} md={12}>
						<Form.Item name="class" label="Select Class" rules={[{ required: true, message: 'Field required!' },]}  >
								<Select placeholder="Select Class" >
										{classList && classList.map((item, index) => <Select.Option key={index} value={item.class_name}>{item.class_name}</Select.Option>)}
									</Select>
						</Form.Item>
					</Col>

					<Col sm={24} md={12}>
						<Form.Item name="subject_id" label="Select Subject" rules={[{ required: true, message: 'Field required!' },]}  >
								<Select placeholder="Select Subject" >
										{subjectArray && subjectArray.map((item, index) => <Select.Option key={index} value={item?._id}>{item?.subject_name}</Select.Option>)}
									</Select>
						</Form.Item>
					</Col>
				</Row>

				<Row gutter={15}>
					<Col sm={24} md={12}>
						<Form.Item name="title" label="Title" rules={[{ required: true, message: 'Field required!' },]}  >
								<Select placeholder="Select Te" >
										{teacherArray && teacherArray.map((item, index) => <Select.Option key={index} value={item?._id}>{item?.username}</Select.Option>)}
									</Select>
						</Form.Item>
					</Col>

					<Col sm={24} md={12}>
						<Form.Item name="date" label="Select Date" rules={[{ required: true, message: 'Field required!' },]}  >
						<DatePicker size="large"  disabledDate={(current) => {
              let customDate = moment().format("YYYY-MM-DD");
              return current && current < moment(customDate, "YYYY-MM-DD");
            }}  format="YYYY-MM-DD"/>
						</Form.Item>
					</Col>
				</Row>

			
				<Row gutter={15}>
					{/* <Col sm={24} md={12}>
