import React from 'react';
import { Link } from 'react-router-dom';
import Apploader from '../../components/loader/loader'
import { connect } from 'dva';
import moment from 'moment';

import { Form, Select, Card, Typography, Alert, Input, Button, Table, Radio, Divider, Switch, Row, Col, Avatar, Pagination, Tabs, Modal, Badge, Popconfirm } from 'antd';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import AddEdit from './action/addEdit';
import { getTitleImage } from '../../utils/functions';
const { Search } = Input;
const { Text } = Typography;
const { TabPane } = Tabs;
const formItemLayout = { labelCol: { xs: { span: 24, }, sm: { span: 24, }, } };



class TimeTableList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			limit: 25,
			current: 1,
			sortBy: 'asc',
			addModel: false,
			inactive: false,
			searchText: '',
			loader: false,
			detail: '',
			count: 0,
			Addcount: 0,
			listData: [],
			stream_list: [],
			year_list: [],
			class_list: [],
			subject_list: [],
			teacher_list: [],


		}

		setTimeout(() => document.title = 'Time Table List', 100,);
		this.isUpdate = false;
	}

	componentDidMount() {
		this.ListFun();



	}



	ListFun = () => {
		this.props.dispatch({ type: 'time_tables/timeTableList', payload: {} });
	}


	ShowSizeChange = (current, size) => this.setState({ limit: size }, () => this.ListFun());
	switchFun = (val) => this.setState({ inactive: val }, () => this.ListFun());
	ChangeOrder = (val) => this.setState({ sortBy: this.state.sortBy === 'asc' ? 'desc' : 'asc' }, () => this.ListFun());

	searchVal = (val) => {
		this.state.searchText = val;
		const resultAutos = this.props.time_tables.list.filter((auto) =>
			auto.title.toLowerCase().includes(val.toLowerCase()) ||
			auto.description.toLowerCase().includes(val.toLowerCase())
		)
		this.setState({ listData: resultAutos })
	}

	createCat = (val) => {
		if (val) { this.ListFun() }
		this.setState({ detail: '', addModel: false })
	}

	deletetime_tables = id => {
		this.props.dispatch({ type: 'time_tables/deleteTimeTable', payload: id });
	}

	getSnapshotBeforeUpdate(prevProps, prevState) {
		if (this.props.time_tables.delete) {
			this.props.dispatch({ type: 'time_tables/clearAction' });
			this.ListFun();
			return true
		}
		return null;
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		if (snapshot) {
		}
	}

	onStreamChange = (e) => {
	}


	render() {
