		const { time_tables } = this.props;
		if (this.state.searchText == '') {
			this.state.listData = time_tables.list ? time_tables.list : [];
		}

		if (allData.length > 0) {
			const streamList = [...new Map(allData.map((item) => [item.streamInfo[0]["name"], item])).values(),];

