				render: (value, row) => {
					return <span>{value?.subject_name}</span>
				}
			},
			{
				title: <strong>Teacher Name</strong>, dataIndex: 'userInfo',
				render: (value, row) => {
					return <span>{value[0]?.username}</span>
				}
			},
			{
				title: <strong>Start Time</strong>, dataIndex: 'startTime',
