					<Col>
						<Button type="primary" onClick={() => this.props.history.push('/time-table/add')}>Add</Button>
					</Col>
				</Row>

				<div className="innerContainer">
					<Card style={{ marginTop: "0" }} bodyStyle={{ padding: '0 15px 15px' }}>
						<Table columns={columns}
							dataSource={this.state.listData}
							rowKey={record => record._id}
							onRow={(record, rowIndex) => {
								return {
									// onClick: event => this.setState({ addModel: true, detail: record })
								};
							}}
							pagination={{
								position: ['bottomLeft'],
								showTotal: (total, range) => <Text type="secondary">{`Showing ${range[0]}-${range[1]} of ${total}`}</Text>, showSizeChanger: true,
								responsive: true,
								onShowSizeChange: (current, size) => this.ShowSizeChange(current, size),
								pageSizeOptions: ['10', '25', '50', '100', '250', '500'],

							}}
							loading={this.props.loading.global}
							title={() => {
								return <>

									<Form ref={this.formRef} {...formItemLayout} className="bg-light" style={{ maxWidth: '100%' }} layout="vertical" onFinish={this.handleLoginSubmit}>
										<Row gutter={15}>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange(e)}>
														{this.state.stream_list && this.state.stream_list.map((item, index) => {

															return <Select.Option key={index} value={item?.streamInfo[0]?._id}>{item?.streamInfo[0]?.name}</Select.Option>
														})}
													</Select>

												</Form.Item>
											</Col>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange2(e)}>
														{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={index}>{index}</Select.Option>)}
													</Select>
												</Form.Item>
											</Col>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange2(e)}>
														{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={index}>{index}</Select.Option>)}
													</Select>
												</Form.Item>
											</Col>
										</Row>
										<Row gutter={15}>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange2(e)}>
														{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={index}>{index}</Select.Option>)}
													</Select>
												</Form.Item>
											</Col>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange2(e)}>
														{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={index}>{index}</Select.Option>)}
													</Select>
												</Form.Item>
											</Col>
											<Col sm={12} md={8}>
												<Form.Item name="stream_id" label="Stream Name" rules={[{ required: true, message: 'Field required!' },]}  >
													<Select placeholder="Select Stream" onChange={(e) => this.onStreamChange2(e)}>
														{streamArray && streamArray.map((item, index) => <Select.Option key={index} value={index}>{index}</Select.Option>)}
													</Select>
												</Form.Item>
											</Col>
										</Row>

										<Form.Item className="mb-0">
											<Button >Reset</Button>&nbsp;&nbsp;
											<Button type="primary" className="btn-w25 btn-primary-light" >Filter</Button>
										</Form.Item>



									</Form>

								</>
							}}


						/>
					</Card>
				</div>

			</div>

		);
	}
};

export default connect(({ time_tables, loading }) => ({
	time_tables, loading
}))(TimeTableList);
